SET NAMES UTF8;
DROP DATABASE IF EXISTS databank;
CREATE DATABASE databank CHARSET=UTF8;
/* 用户表 */
USE databank;
create table user_data(
  uid INT PRIMARY KEY AUTO_INCREMENT,
  uname varchar(32),
  upwd  varchar(32),
  CompanyName varchar(32),
  CompanyFax varchar(32),
  CompanyContactAddress varchar(64),
  zipCode varchar(8),
  CompanyWebSute varchar(64),
  phone char(11)
);
